<script>
var idnewPass = $('#password');
var tooltip = $('#tooltipStrength');
tooltip.tooltipster({
    theme: 'tooltipster-shadow',
    trigger: 'click',
});

function validateEmail(email) {
    var re = /^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;
    return re.test(email);
}
function submit_register(fb_uid){
    var email = $('input[name=email').val();
    $('.loading').show();
    if(email){
        if(validateEmail(email)){
            var data = $('form#submit-consumer-registration').serialize();
            if(fb_uid) data+='&fb_uid='+fb_uid;
            $.post('/acc123/registration', data, function(result){
                $('.loading').hide();
                $("input[name='data[_Token][key]']").val(result.key);
                if(result.code!=200){
                    $('.login-form .form-group').removeClass('has-error');
                    if(result.code==204){
                        $('.login-form div#reg-danger').html(result.message);
                        $("input[name='"+result.type+"']").parent().parent().addClass('has-error');
                    }else if(result.code==203){
                        var message = ""; var ii = 0;
                        $.each(result.message,function(index,row){
                            ii++;
                            message+= row.msg;
                            if(ii<result.message.length) message+=", ";
                            $("input[name='"+row.field+"']").parent().parent().addClass('has-error');
                        });
                        $('.login-form div#reg-danger').html(message);
                    };
                    $('#reg-danger').show();
                    $('#reg-success').hide();
                }else{
                    //$('> div.alert-success').html(result.message);
                    if(fb_uid){
                        window.location.href = result.url_redirect;
                    }else{
                        $('#reg-danger').hide();
                        $('#reg-success').show();
                        $('.login-form .form-group').removeClass('has-error');
                    }
                }
            },'json');
        }else{
            $('.loading').hide();
            $('#reg-danger').show();
            $('#reg-success').hide();
            $("input[name=email]").parent().parent().addClass('has-error');
            $('.login-form div.alert-danger').html('Email tidak valid');
        }
    }else{
        $('.loading').hide();
        $('#reg-danger').show();
        $('#reg-success').hide();
        $("input[name=email]").parent().parent().addClass('has-error');
        $('.login-form div.alert-danger').html('Email tidak boleh kosong');
    }
}

function submitRegister(){
    var pwd_meter = $('#pwd_meter').html();
    if(idnewPass.val() != ''){
        if(pwd_meter <= 16){
            tooltip.tooltipster('show');
            return false;
        }
    }

    $('.alert-success').hide();
    $('.alert-danger').hide();
    var fb_uid = $('#RegisterByFb').attr('data-fb-uid');
    if(fb_uid){
        console.log('submit with fb');
        submit_register(fb_uid);
    }else{
        console.log('submit without fb');
        submit_register('');
    }
    return false;
}

function strengthPass(pass) {
    if(pass == ''){
        $('.strength-meter').hide();
    } else {
        $('.strength-meter').show();
    }

    var strPass = check_password(pass);
    $('#pwd_bar').css({
        'width': (strPass['value']*2)+'%',
        'background-color' : strPass['barColor']
    });
    $('#pwd_desc').html(strPass['strDesc']);
    tooltip.tooltipster('content', 'password tidak boleh mengandung '+strPass['strDesc'])
    $('#pwd_meter').html(strPass['value']);
}

