<script>
	function isset(data, data_not_exist){
		if(typeof(data) != "undefined" && data !== null) {
			return data;
		} else {
			return data_not_exist;
		}
	}

	function changeScoreMood(score){
		$('*[data-mood="1"]').find('span').html(isset(score['terinspirasi'],0)+'%');
		$('*[data-mood="2"]').find('span').html(isset(score['terhibur'],0)+'%');
		$('*[data-mood="3"]').find('span').html(isset(score['biasa saja'],0)+'%');
		$('*[data-mood="4"]').find('span').html(isset(score['tidak menarik'],0)+'%');
		$('*[data-mood="5"]').find('span').html(isset(score['terganggu'],0)+'%');
		$('*[data-mood="6"]').find('span').html(isset(score['tidak suka'],0)+'%');
	}

	$(document).ready(function(){
		$('.mood-box-wrapper').click(function(){
			var moodClick = $(this);
			$.get( "votemood/"+$(this).data('mood')+"-55309")
			.done(function(data) {
				data = JSON.parse(data);
				if(data.status == 'success'){
					$('.mood-box-wrapper').addClass('mood-inactive').removeClass('mood-active');
					moodClick.addClass('mood-active').removeClass('mood-inactive');
					changeScoreMood(data.score);
				} else if(data.status == 'voted'){
					$('.mood-box-wrapper').addClass('mood-inactive').removeClass('mood-active');
					$('*[data-mood="'+data.vote+'"]').addClass('mood-active').removeClass('mood-inactive');
					changeScoreMood(data.score);
				} else if(data.status == 'not login') {
					$('#LoginAgentModal-new').modal({
						show: 'false'
					}); 
				} else {
					console.log('fail vote');
				}
			}).fail(function(data) {
				console.log('fail');
			});
        });
    });

</script>

